import React from "react";

const ProjectItem = ({ title, description, keyAspects }) => (
  <div className="mb-8 p-6 bg-gray-50 rounded-lg shadow-md">
    <h3 className="text-2xl font-semibold mb-3">{title}</h3>
    <p className="text-gray-700 mb-4">{description}</p>
    {keyAspects.map((aspect, index) => (
      <div key={index} className="mb-3">
        <h4 className="text-lg font-medium mb-2">{aspect.title}:</h4>
        {aspect.type === "list" ? (
          <ul className="list-disc pl-5">
            {aspect.items.map((item, itemIndex) => (
              <li key={itemIndex} className="text-gray-600 mb-1">
                {item}
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-gray-600">{aspect.content}</p>
        )}
      </div>
    ))}
  </div>
);

function Projects() {
  const projects = [
    {
      title: "AI-Powered Content Recommendation Engine",
      description:
        "I developed an AI-powered content recommendation engine for healthcare platform. This project aimed to personalize user experience and increase engagement by suggesting relevant medical articles, videos, and resources to patients and healthcare providers.",
      keyAspects: [
        {
          title: "Tech Stack",
          type: "list",
          items: [
            "Backend: Python (Flask) for API development",
            "Frontend: React for the user interface",
            "Database: MongoDB for storing user data and content",
            "Machine Learning: TensorFlow for the recommendation algorithm",
            "Cloud Infrastructure: AWS for deployment and scaling",
          ],
        },
        {
          title: "Features",
          type: "list",
          items: [
            "Real-time content recommendations based on user behavior and preferences",
            "A/B testing framework to continuously improve recommendation accuracy",
            "RESTful API for easy integration with mobile apps (React Native)",
            "Admin dashboard for content management and performance analytics",
          ],
        },
        {
          title: "Challenges and Solutions",
          type: "list",
          items: [
            "Scalability: Implemented data sharding and caching mechanisms to handle millions of users and content items",
            "Privacy: Developed a robust anonymization process to ensure HIPAA compliance",
            "Cold Start Problem: Created a hybrid recommendation system combining content-based and collaborative filtering approaches",
          ],
        },
      ],
    },
    {
      title: "Hackathon Project: AI-Powered Issue Resolution Assistant",
      description:
        "Building on a successful RAG project that retrieves similar JIRA ticket information, this hackathon project aimed to take the issue resolution process to the next level by developing a prototype for an advanced AI assistant.",
      keyAspects: [
        {
          title: "Key Objectives",
          type: "list",
          items: [
            "Quick Setup: Leveraged existing RAG system to get a running start",
            "Rapid Prototyping: Implemented key features demonstrating significant improvement over the current system",
            "Demo-Ready UI: Created a simple but effective user interface to showcase the project's capabilities",
            "Data-Driven Insights: Used real (anonymized) data from JIRA system to demonstrate tangible benefits",
          ],
        },
        {
          title: "Implemented Features",
          type: "list",
          items: [
            "Resolution Pathway Suggestion: Analyzed and suggested steps to resolve new issues based on past successes",
            "Expert Recommender: Identified team members who've solved similar issues before",
            "Simple Predictive Model: Implemented a basic model to predict potential issues based on historical data",
            "Interactive Chat Interface: Enhanced the existing chat interface to handle more complex queries about issue trends or expert skills",
          ],
        },
        {
          title: "Hackathon Deliverables",
          type: "list",
          items: [
            "Working Prototype: A functional demo of the chosen features",
            "Brief Presentation: A 5-minute pitch explaining the problem, solution, and potential impact",
            "Future Roadmap: Ideas for further development post-hackathon",
          ],
        },
        {
          title: "Impact",
          type: "content",
          content:
            "This hackathon project demonstrated the potential for significantly improving the team's issue resolution process, combining AI technology with practical, immediate benefits for the development workflow.",
        },
      ],
    },
    {
      title: "AI-Powered Chatbot",
      description:
        "Developed a chatbot using natural language processing techniques to provide automated customer support for an e-commerce platform. The chatbot successfully handled 70% of customer queries without human intervention.",
      keyAspects: [
        {
          title: "Technologies used",
          type: "content",
          content: "Python, TensorFlow, Flask, DialogFlow",
        },
      ],
    },
    {
      title: "Data Visualization Dashboard",
      description:
        "Created an interactive dashboard to visualize complex datasets. The dashboard allows users to explore data through various charts and graphs, with features like filtering and drill-down capabilities.",
      keyAspects: [
        {
          title: "Technologies used",
          type: "content",
          content: "React, D3.js, Node.js, Express, MongoDB",
        },
      ],
    },
  ];

  return (
    <section className="mb-12 bg-white shadow-md rounded-lg p-6">
      <h2 className="text-3xl font-semibold mb-8">Projects</h2>
      {projects.map((project, index) => (
        <ProjectItem key={index} {...project} />
      ))}
    </section>
  );
}

export default Projects;
